mapper.select:
select *
from web5_member
where id like #{id}

mapper.update:
update web5_member
set
password = #{password}, name=#{name}, phone=#{phone},
address=#{address}, email=#{email}
where id like #{id}

mapper.insert:
insert into web5_board
(boardnum, id, title, contents

	,originalfile, savedfile

)
values
(web5_board_seq.nextval, #{id}, #{title}, #{contents}

	,#{originalfile}, #{savedfile}

)
